mapper.insert:

    SELECT professor_seq.NEXTVAL FROM dual

    INSERT INTO professor(
    id, professor_num, created_at, ended_at, dept_id, user_id
)
    VALUES(
    #{id}, #{professorNum}, #{createdAt}, #{endedAt}, #{deptId}, #{userId}
)

mapper.update:
UPDATE professor
professor_num = #{professorNum}, created_at = #{createdAt}, ended_at = #{endedAt}, dept_id = #{deptId}, user_id = #{userId}
WHERE id = #{id}
